var db = JSON.parse(localStorage.getItem('dbCamilaCampos'));
if (!db) {
    db = dados
};

// Exibe mensagem em um elemento de ID msg
function displayMessage(msg) {
    $('#msg').html('<div class="alert alert-warning">' + msg + '</div>');
}

function insertPost(post) {
    // Calcula novo Id a partir do último código existente no array
    let novoId = db.posts[db.posts.length - 1].id + 1;
    let novoPost = {
        "id": novoId,
        "titulo": post.titulo,
        "img": post.img,
        "texto": post.texto,
        "data": post.data,
        "comentarios": [{id: -1, nome: "dory", textoComent: "procurando dory"}]

  };

    //Insere o novo obejto no array
    db.posts.push(novoPost);
    displayMessage("Post realizado com sucesso");

    //Atualiza os dados no Local Storage
    localStorage.setItem('dbCamilaCampos', JSON.stringify(db));
}


function insertComentario(comentario) {
    for (i = 0; i < db.posts.length; i++) {
        if (db.posts[i].id == id) {

            // Calcula novo Id a partir do último código existente no array
            let novoId = db.posts[i].comentarios[db.posts[i].comentarios.length - 1].id + 1;
            let novoComentario = {
                "id": novoId,
                "nome": comentario.nome,
                "textoComent": comentario.textoComent
                
            }

        //Insere o novo obejto no array
        db.posts[i].comentarios.push(novoComentario);
        displayMessage("Comentário realizado com sucesso");

        //Atualiza os dados no Local Storage
        localStorage.setItem('dbCamilaCampos', JSON.stringify(db));
        break;
        }
    }
}

